svg.script:

(function() {
  const lines = [
    { id: 'l1', text: 'I build practical, scalable web applications and automation systems', delay: 0 },
    { id: 'l2', text: 'that solve real operational and business problems.', delay: 2000 },
    { id: 'l3', text: 'With a systems-heavy electrical engineering background,', delay: 3500 },
    { id: 'l4', text: 'I bring a reliability-first and process-driven mindset into software development.', delay: 5500 }
  ];
  
  function typeText(element, text, speed = 30) {
    let i = 0;
    element.textContent = '';
    
    return new Promise((resolve) => {
      const interval = setInterval(() => {
        if (i < text.length) {
          element.textContent += text.charAt(i);
          i++;
        } else {
          clearInterval(interval);
          resolve();
        }
      }, speed);
    });
  }
  
  function startAnimation() {
    lines.forEach(line => {
      setTimeout(() => {
        const element = document.getElementById(line.id);
        if (element) {
          typeText(element, line.text);
        }
      }, line.delay);
    });
  }
  
  // Start animation immediately
  startAnimation();
  
  // Restart animation every 10 seconds
  setInterval(startAnimation, 10000);
})();
